const initState={
    notes:[
        {id:0, nome:"spesa", descrizione:"latte, uova, pollo"},
        {id:1, nome:"esercizi di scuola", descrizione:"matematica pagina 67-68, storia la rivoluzione francese"}
    ]
}




const rootReducer = (state = initState, action) => {
    if (action.type === 'CHANGE_STATE') {
      const newState = state.notes;
      newState.map((note) => {
        if (note.id === action.id) {
          note.descrizione = action.change;
          note.nome = action.nome;
        }
      });
      return {
        ...state,
        notes: newState,
      };
    } else if (action.type === 'ADD_NOTE') {
      const newNote = {
        id: Math.random(),
        nome: 'new note',
        descrizione: '.',
      };
      firestore.collection('notes').add(newNote)
        .then(() => console.log('Nota aggiunta con successo al database'))
        .catch((error) => console.error('Errore durante l\'aggiunta della nota:', error));
  
      const newNotes = [...state.notes, newNote];
      return {
        ...state,
        notes: newNotes,
      };
    } else if (action.type === 'DELETE_NOTE') {
      firestore.collection('notes').doc(action.id).delete()
        .then(() => console.log('Nota eliminata con successo dal database'))
        .catch((error) => console.error('Errore durante l\'eliminazione della nota:', error));
  
      const newState = state.notes.filter((note) => note.id !== action.id);
      return {
        ...state,
        notes: newState,
      };
    }
    return state;
  };
  
  export default rootReducer;
